import moment from 'moment';
import { nanoid } from 'nanoid';
import React, { useState } from 'react';
import { Wave } from '../../model/wave';
import { Transaction } from '../transaction';
import styles from './index.module.scss';

const wavesList: Wave[] = [
	{
		id: nanoid(),
		walletId: "0xC5581F1aE61E34391824779D505Ca127a4566737",
		messageText: "hello! Making my mark on the Ethereum blockchain",
		timestamp: moment().utc().format().toString()
	},
	{
		id: nanoid(),
		walletId: "0xC5581F1aE61E34391824779D505Ca127a4566737",
		messageText: "Nice! This is awesome stuff",
		timestamp: moment().utc().format().toString()
	},
	{
		id: nanoid(),
		walletId: "0xC5581F1aE61E34391824779D505Ca127a4566737",
		messageText: "wave portal. A portal to the etherum blockchain. a social project",
		timestamp: moment().utc().format().toString()
	}
]



export const MainBody = () => {
	const initialWaves: Wave[] = [];

	const [messageText, setMessageText] = useState<string>('');
	const [waves, setWaves] = useState<Wave[]>(initialWaves);

	const handleTextChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		setMessageText(e.target.value);
	};	

	const handleClick = () => {
		if (messageText === '') { return };

		const newWave: Wave = {
			id: nanoid(),
			walletId: "0xC5581F1aE61E34391824779D505Ca127a4566737",
			messageText, 
			timestamp: moment().utc().format(),
		}

		const newWaves = waves
			.concat(newWave)
			.sort(function(a, b) {
				return a.timestamp < b.timestamp ? 1: -1;
			});

		setWaves(newWaves);
		setMessageText('');
		console.log('got to the end');
	};

	const renderHeader = () => (
		<div className={styles.headerContainer}>
			<div className={styles.header}>Waveportal</div>
		</div>
	);

	const renderBody = () => (
		<div className={styles.bodyContainer}>
			<div className={styles.textContainer}>
				Connect your wallet and leave a message!
			</div>

			<div className={styles.textInputContainer}>
				<input
					className={styles.textInput}
					type="text"
					value={messageText}
					placeholder="leave a message!"
					onChange={handleTextChange}
				/>
			</div>

			<div className={styles.buttonContainer}>
				<div className={styles.waveButton} onClick={handleClick}>
					<div className={styles.buttonText}>wave</div>
				</div>
			</div>
		</div>
	);

	const renderTransactions = () => 
		waves.map((w: Wave) => {
			<Transaction key={w.id} wave={w}/>
		});



	return (
		<div className={styles.container}>
			<div className={styles.dataContainer}>
				{renderHeader()}
				{renderBody()}
				<div className={styles.transactionsContainer}>
					{renderTransactions()}
				</div>
			</div>
		</div>
	);
};
