import { BigNumber, ethers } from 'ethers';
import { CONTACT_ADDRESS } from '../util/constants';
import contractABI from '../util/WavePortal.json';
import { nanoid } from 'nanoid';
import moment from 'moment';
import { Wave } from '../model/wave';

export const createWaveTxn = async (messageText: string) => {
  try {
    const { ethereum }: any = window;
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);

    if (contract !== undefined) {
      const newWave: Wave = {
        id: nanoid(),
        walletId: signer.toString(),
        messageText, 
        timestamp: moment().utc().format(),
      }

      const waveTxn = await contract.sendWave(newWave.timestamp, newWave.messageText);
      console.log('Mining... ', waveTxn.hash);

      await waveTxn.wait();
      console.log('Mined--', waveTxn.hash);

      return newWave;
    }
  } catch (error) {
    console.log(error);
    return undefined;
  }
};

export const getWaveCount = async () => {
  try {
    const { ethereum }: any = window;
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);

    const count = await contract.getTotalWaves();
    console.log('returned total waves count: ', count);
    return count;
  } catch (error) {
    console.log(error);
    return undefined;
  }
}

export const getAllWaves = async () => {
  try {
    const { ethereum }: any = window;
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);
    const numWaves = await getWaveCount();

    let retrievedWaves: Wave[] = []

    for (let i = 0; i < numWaves; i++) {
      const [ 
        txnIndex, 
        utcTimeStamp, 
        message,
        senderWalletAddress
      ] = await contract.waves(i);

      const wave: Wave = {
        id: BigNumber.from(txnIndex).toString(),
        walletId: senderWalletAddress,
        messageText: message,
        timestamp: utcTimeStamp,
      }

      retrievedWaves.push(wave);
    }
    return retrievedWaves;

  } catch (error) {
    console.log(error);
    return undefined;
  }
}
