import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { Wave } from '../../model/wave';
import { Transaction } from '../transaction';
import styles from './index.module.scss';
import { EthereumService }  from '../../service';
import { CONTACT_ADDRESS } from '../../util/constants';
import contractABI from '../../util/WavePortal.json';

const initialWaves: Wave[] = [];
declare let window: any;

interface Props {
	account: any;
	connectWallet: any;
}

export const MainBody = (props: Props) => {
	const [messageText, setMessageText] = useState<string>('');
	const [waves, setWaves] = useState<Wave[]>(initialWaves);
	const [totalWaves, setTotalWaves] = useState<number>(0);

	const handleTextChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		setMessageText(e.target.value);
	};

	const handleClick = async () => {
		if (messageText === '') { return };
		const waves = await EthereumService.createWaveTxn(messageText);
		setMessageText('');
	};

	const listenForNewWaves = async () => {
		console.log('listening for new waves!');
		const { ethereum } = window;
		try {
			if (window.ethereum) {
				const provider = new ethers.providers.Web3Provider(ethereum);
				const signer = provider.getSigner();
				const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);

				contract.on('NewWave', (from, timestamp, message) => {
					console.log('NewWave', from, timestamp, message);

					setWaves(prevState => [...prevState, {
						address: from,
						timestamp: new Date(timestamp * 1000),
						message: message
					} as Wave]);
				});


			} else {
				console.log('Ethereum object does not exist!'); 
			}
		} catch (error) {
			console.log(error);
		}
	};

	const handleGetAllWaves = async () => {
		const wavesResponse = await EthereumService.getAllWaves();
		if (wavesResponse !== undefined) {
			setWaves(wavesResponse);
		}
	};

	const handleGetWaveCount = async () => {
		await EthereumService.getWaveCount();
	};


	useEffect(() => {
		handleGetWaveCount();
		handleGetAllWaves();
	}, []);

	useEffect(() => {
		listenForNewWaves();
	}, []);

	const renderHeader = () => (
		<div className={styles.headerContainer}>
			<div className={styles.header}>Waveportal</div>
		</div>
	);

	const renderConnectWalletButton = () => (
		<div className={styles.buttonContainer}>
			<div className={styles.waveButton} onClick={props.connectWallet}>
				<div className={styles.buttonText}>connect wallet</div>
			</div>
		</div>
	);

	const renderTextFieldAndWaveButton = () => (
		<div>
			<div className={styles.textInputContainer}>
				<input
					className={styles.textInput}
					type="text"
					value={messageText}
					placeholder="leave a message!"
					onChange={handleTextChange}
				/>
			</div>

			<div className={styles.buttonContainer}>
				<div className={styles.waveButton} onClick={handleClick}>
					<div className={styles.buttonText}>wave</div>
				</div>
			</div>
		</div>
	);

	const renderBody = () => (
		<div className={styles.bodyContainer}>
			<div className={styles.textContainer}>
				Connect your wallet and leave a message!
			</div>
			{!props.account ? renderConnectWalletButton() : renderTextFieldAndWaveButton()}
		</div>
	);

	return (
		<div className={styles.container}>
			<div className={styles.dataContainer}>
				{renderHeader()}
				<div className={styles.countContainer}>
					<div>{`So far ${totalWaves} waves have been sent!`} </div>
				</div>
				{renderBody()}
				{waves.length !== 0 
						? <div className={styles.transactionsContainer}>
								{waves.map((wave: Wave) => 
									<Transaction key={wave.address} wave={wave}/>
								)}
							</div>
						: null}
			</div>
		</div>
	);
};
