import moment from 'moment';
import { nanoid } from 'nanoid';
import React, { useState, useEffect } from 'react';
import { Wave } from '../../model/wave';
import { Transaction } from '../transaction';
import styles from './index.module.scss';

import { ethers } from 'ethers';
import { CONTACT_ADDRESS } from '../../util/constants';
import contractABI from '../../util/WavePortal.json';

const initialWaves: Wave[] = [];

interface Props {
	account: any;
	connectWallet: any;
}

export const MainBody = (props: Props) => {
	const [messageText, setMessageText] = useState<string>('');
	const [waves, setWaves] = useState<Wave[]>(initialWaves);
	const [totalWaves, setTotalWaves] = useState<number>(0);

	const handleTextChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		setMessageText(e.target.value);
	};

	const handleClick = async () => {
		if (messageText === '') { return };
		await createWaveTxn(messageText);
	};

	const createWaveTxn = async (messageText: string) => {
		try {
			const { ethereum }: any = window;
			const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);

			if (contract !== undefined) {
				const newWave: Wave = {
					id: nanoid(),
					walletId: signer.toString(),
					messageText, 
					timestamp: moment().utc().format(),
				}

				let count = await contract.getTotalWaves();
				console.log("retrieved total wave count", count.toNumber());

				const waveTxn = await contract.sendWave(newWave.timestamp, newWave.messageText);
				console.log('Mining... ', waveTxn.hash);

				await waveTxn.wait();
				console.log('Mined--', waveTxn.hash);

				count = await contract.getTotalWaves();
				console.log('retrieved total wave count...', count.toNumber());

				await getAllWaves();
			}
		} catch (error) {
			console.log(error);
		}
	};

	const getWaveCount = async () => {
		try {
			const { ethereum }: any = window;
			const provider = new ethers.providers.Web3Provider(ethereum);
			const signer = provider.getSigner();
			const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);

			const count = await contract.getTotalWaves();
			console.log('returned total waves count: ', count);
			setTotalWaves(count);
			return count;
		} catch (error) {
			console.log(error);
		}
	}

	const getAllWaves = async () => {
		const numWaves = await getWaveCount();

		try {
			const { ethereum }: any = window;
			const provider = new ethers.providers.Web3Provider(ethereum);
			const signer = provider.getSigner();
			const contract = new ethers.Contract(CONTACT_ADDRESS, contractABI.abi, signer);

			let retrievedWaves: Wave[] = []

			console.log("num Waves: ", totalWaves);

			for (let i = 0; i < numWaves; i++) {
				const w = await contract.waves(i);
				const [a, b] = w;
				console.log("a: ", a);
				console.log("b: ", b);
			}

		} catch (error) {
			console.log(error);
		}
	}

	const handleGetWavesNum = async () => {
		const count = await getWaveCount();
	}

	const renderHeader = () => (
		<div className={styles.headerContainer}>
			<div className={styles.header}>Waveportal</div>
		</div>
	);

	const renderConnectWalletButton = () => (
		<div className={styles.buttonContainer}>
			<div className={styles.waveButton} onClick={props.connectWallet}>
				<div className={styles.buttonText}>connect wallet</div>
			</div>
		</div>
	);

	const renderTextFieldAndWaveButton = () => (
		<div>
			<div className={styles.textInputContainer}>
				<input
					className={styles.textInput}
					type="text"
					value={messageText}
					placeholder="leave a message!"
					onChange={handleTextChange}
				/>
			</div>

			<div className={styles.buttonContainer}>
				<div className={styles.waveButton} onClick={handleClick}>
					<div className={styles.buttonText}>wave</div>
				</div>
			</div>
		</div>
	);

	const renderBody = () => (
		<div className={styles.bodyContainer}>
			<div className={styles.textContainer}>
				Connect your wallet and leave a message!
			</div>
			{!props.account ? renderConnectWalletButton() : renderTextFieldAndWaveButton()}
		</div>
	);

	return (
		<div className={styles.container}>
			<div className={styles.dataContainer}>
				{renderHeader()}
				{renderBody()}
				<div className={styles.transactionsContainer}>
					{waves.map((w: Wave) => 
					<Transaction key={w.id} wave={w}/>
				)}
				<button onClick={handleGetWavesNum}>getNumWaves</button>
				<button onClick={getAllWaves}>getAllWaves</button>
				</div>
			</div>
		</div>
	);
};
