import React, { useState } from 'react';
import { Wave } from '../../model/wave';
import styles from './index.module.scss';

export const MainBody = () => {
	const [messageText, setMessageText] = useState<string>('');
	const [waves, setWaves] = useState<Wave, null>(null);

	const handleTextChange = (e: React.ChangeEvent<HTMLInputElement>) => {
		setMessageText(e.target.value);
	};	

	const handleClick = () => {
		setMessageText('');
	};

	const renderHeader = () => (
		<div className={styles.headerContainer}>
			<div className={styles.header}>Waveportal</div>
		</div>
	);

	const renderBody = () => (
		<div className={styles.bodyContainer}>
			<div className={styles.textContainer}>
				Connect your wallet and leave a message!
			</div>

			<div className={styles.textInputContainer}>
				<input
					className={styles.textInput}
					type="text"
					value={messageText}
					placeholder="leave a message!"
					onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
						handleTextChange(e)
					}
				/>
			</div>

			<div className={styles.buttonContainer}>
				<div className={styles.waveButton} onClick={handleClick}>
					<div className={styles.buttonText}>wave</div>
				</div>
			</div>
		</div>
	);

	return (
		<div className={styles.container}>
			<div className={styles.dataContainer}>
				{renderHeader()}
				{renderBody()}
			</div>
		</div>
	);
};
